using System;
using System.Threading.Tasks;
using Newtonsoft.Json;

namespace TdLib
{
    /// <summary>
    /// Autogenerated TDLib APIs
    /// </summary>
    public static partial class TdApi
    {
        /// <summary>
        /// Loads an asynchronous or a zoomed in statistical graph
        /// </summary>
        public class GetStatisticalGraph : Function<StatisticalGraph>
        {
            /// <summary>
            /// Data type for serialization
            /// </summary>
            [JsonProperty("@type")]
            public override string DataType { get; set; } = "getStatisticalGraph";

            /// <summary>
            /// Extra data attached to the function
            /// </summary>
            [JsonProperty("@extra")]
            public override string Extra { get; set; }

            /// <summary>
            /// Chat identifier
            /// </summary>
            [JsonConverter(typeof(Converter))]
            [JsonProperty("chat_id")]
            public long ChatId { get; set; }

            /// <summary>
            /// The token for graph loading
            /// </summary>
            [JsonConverter(typeof(Converter))]
            [JsonProperty("token")]
            public string Token { get; set; }

            /// <summary>
            /// X-value for zoomed in graph or 0 otherwise
            /// </summary>
            [JsonConverter(typeof(Converter))]
            [JsonProperty("x")]
            public long X { get; set; }
        }

        /// <summary>
        /// Loads an asynchronous or a zoomed in statistical graph
        /// </summary>
        public static Task<StatisticalGraph> GetStatisticalGraphAsync(
            this Client client, long chatId = default, string token = default, long x = default)
        {
            return client.ExecuteAsync(new GetStatisticalGraph
            {
                ChatId = chatId, Token = token, X = x
            });
        }
    }
}