using System;
using System.Threading.Tasks;
using Newtonsoft.Json;

namespace TdLib
{
    /// <summary>
    /// Autogenerated TDLib APIs
    /// </summary>
    public static partial class TdApi
    {
        /// <summary>
        /// Sets the list of commands supported by the bot; for bots only
        /// </summary>
        public class SetCommands : Function<Ok>
        {
            /// <summary>
            /// Data type for serialization
            /// </summary>
            [JsonProperty("@type")]
            public override string DataType { get; set; } = "setCommands";

            /// <summary>
            /// Extra data attached to the function
            /// </summary>
            [JsonProperty("@extra")]
            public override string Extra { get; set; }

            /// <summary>
            /// List of the bot's commands
            /// </summary>
            [JsonConverter(typeof(Converter))]
            [JsonProperty("commands")]
            public BotCommand[] Commands { get; set; }
        }

        /// <summary>
        /// Sets the list of commands supported by the bot; for bots only
        /// </summary>
        public static Task<Ok> SetCommandsAsync(
            this Client client, BotCommand[] commands = default)
        {
            return client.ExecuteAsync(new SetCommands
            {
                Commands = commands
            });
        }
    }
}